import React, { useContext, useState } from 'react';
import {
  LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer
} from 'recharts';
import { useData } from './services/DataContext'; // Correct import

const DashboardApp = () => {
  const { data, loading, error } = useData(); // Use the useData hook
  const [filters, setFilters] = useState({
    year: 'all', topic: 'all', region: 'all', pestle: 'all', source: 'all', swot: 'all', country: 'all', city: 'all'
  });

  // Check for loading state or error
  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error.message}</div>;
  if (!data || data.length === 0) return <div>No data available</div>; // Handle empty data

  // Filter data based on selected filters
  const filteredData = data.filter(item => {
    return (
      (filters.year === 'all' || item.year === parseInt(filters.year)) &&
      (filters.topic === 'all' || item.topic === filters.topic) &&
      (filters.region === 'all' || item.region === filters.region) &&
      (filters.pestle === 'all' || item.pestle === filters.pestle) &&
      (filters.source === 'all' || item.source === filters.source) &&
      (filters.swot === 'all' || item.swot === filters.swot) &&
      (filters.country === 'all' || item.country === filters.country) &&
      (filters.city === 'all' || item.city === filters.city)
    );
  });

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8">Data Analysis Dashboard</h1>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        {/* Select components for filters go here */}
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={filteredData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="year" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="intensity" stroke="#3b82f6" strokeWidth={2} />
              <Line type="monotone" dataKey="likelihood" stroke="#10b981" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Other charts go here... */}
      </div>
    </div>
  );
};

export default DashboardApp;
