import React from 'react';
import { useData } from '../services/DataContext';
import { Line, Bar, Pie } from 'react-chartjs-2';
import { Chart as ChartJS } from 'chart.js/auto';

const DashboardApp = () => {
  const { data, filters, setFilters } = useData(); // Access data and filters from context

  // Prepare data for charts
  const lineData = {
    labels: data.map((item) => item.year),
    datasets: [
      {
        label: 'Intensity',
        data: data.map((item) => item.intensity),
        borderColor: '#3b82f6',
        backgroundColor: 'rgba(59, 130, 246, 0.3)',
        fill: true,
      },
      {
        label: 'Likelihood',
        data: data.map((item) => item.likelihood),
        borderColor: '#10b981',
        backgroundColor: 'rgba(16, 185, 129, 0.3)',
        fill: true,
      },
    ],
  };

  const barData = {
    labels: data.map((item) => item.region),
    datasets: [
      {
        label: 'Relevance',
        data: data.map((item) => item.relevance),
        backgroundColor: '#8b5cf6',
      },
    ],
  };

  const pieData = {
    labels: data.map((item) => item.topic),
    datasets: [
      {
        data: data.map((item) => item.relevance),
        backgroundColor: ['#0088FE', '#00C49F', '#FFBB28'],
      },
    ],
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8">Data Analysis Dashboard</h1>

      {/* Line Chart for Intensity & Likelihood */}
      <div className="bg-white shadow rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
        <Line data={lineData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>

      {/* Bar Chart for Relevance by Region */}
      <div className="bg-white shadow rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
        <Bar data={barData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>

      {/* Pie Chart for Topics Distribution */}
      <div className="bg-white shadow rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Topics Distribution</h2>
        <Pie data={pieData} options={{ responsive: true, maintainAspectRatio: false }} />
      </div>
    </div>
  );
};

export default DashboardApp;
