import React, { useState } from "react";

const DashboardApp = () => {
  // Define initial state for each filter
  const [filters, setFilters] = useState({
    endYear: "all",
    topics: "all",
    sector: "all",
    region: "all",
    pest: "all",
    source: "all",
    swot: "all",
    country: "all",
    city: "all",
  });

  // Example filter options (you can replace these with actual values from your backend)
  const filterOptions = {
    endYear: ["all", "2020", "2021", "2022", "2023"],
    topics: ["all", "Technology", "Business", "Science"],
    sector: ["all", "Healthcare", "Finance", "Education"],
    region: ["all", "North America", "Europe", "Asia"],
    pest: ["all", "Political", "Economic", "Social", "Technological"],
    source: ["all", "Research", "Survey", "Report"],
    swot: ["all", "Strength", "Weakness", "Opportunity", "Threat"],
    countries: ["all", "USA", "Canada", "India", "UK", "Germany"],
    cities: ["all", "New York", "London", "Berlin", "Toronto", "Mumbai"],
  };

  // Handle filter change
  const handleFilterChange = (e, filterType) => {
    setFilters({ ...filters, [filterType]: e.target.value });
  };

  // Reset all filters
  const handleReset = () => {
    setFilters({
      endYear: "all",
      topics: "all",
      sector: "all",
      region: "all",
      pest: "all",
      source: "all",
      swot: "all",
      country: "all",
      city: "all",
    });
  };

  return (
    <div>
      <h1>Dashboard Filters</h1>
      
      {/* Filter UI */}
      <div className="filters">
        <select value={filters.endYear} onChange={(e) => handleFilterChange(e, 'endYear')} className="p-2 border rounded">
          {filterOptions.endYear.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>

        <select value={filters.topics} onChange={(e) => handleFilterChange(e, 'topics')} className="p-2 border rounded">
          {filterOptions.topics.map(topic => (
            <option key={topic} value={topic}>{topic}</option>
          ))}
        </select>

        <select value={filters.sector} onChange={(e) => handleFilterChange(e, 'sector')} className="p-2 border rounded">
          {filterOptions.sector.map(sector => (
            <option key={sector} value={sector}>{sector}</option>
          ))}
        </select>

        <select value={filters.region} onChange={(e) => handleFilterChange(e, 'region')} className="p-2 border rounded">
          {filterOptions.region.map(region => (
            <option key={region} value={region}>{region}</option>
          ))}
        </select>

        <select value={filters.pest} onChange={(e) => handleFilterChange(e, 'pest')} className="p-2 border rounded">
          {filterOptions.pest.map(pest => (
            <option key={pest} value={pest}>{pest}</option>
          ))}
        </select>

        <select value={filters.source} onChange={(e) => handleFilterChange(e, 'source')} className="p-2 border rounded">
          {filterOptions.source.map(source => (
            <option key={source} value={source}>{source}</option>
          ))}
        </select>

        <select value={filters.swot} onChange={(e) => handleFilterChange(e, 'swot')} className="p-2 border rounded">
          {filterOptions.swot.map(swot => (
            <option key={swot} value={swot}>{swot}</option>
          ))}
        </select>

        <select value={filters.country} onChange={(e) => handleFilterChange(e, 'country')} className="p-2 border rounded">
          {filterOptions.countries.map(country => (
            <option key={country} value={country}>{country}</option>
          ))}
        </select>

        <select value={filters.city} onChange={(e) => handleFilterChange(e, 'city')} className="p-2 border rounded">
          {filterOptions.cities.map(city => (
            <option key={city} value={city}>{city}</option>
          ))}
        </select>

        <button className="p-2 border rounded bg-gray-200 hover:bg-gray-300" onClick={handleReset}>Reset Filters</button>
      </div>
    </div>
  );
};

export default DashboardApp;
