import React, { useState, useMemo } from 'react';
import { useData } from '../services/DataContext';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
  RadialLinearScale,
} from 'chart.js';
import { Line, Bar, Pie, Doughnut, Radar } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  RadialLinearScale,
  Title,
  Tooltip,
  Legend
);

const DashboardApp = () => {
  const { data, filters, setFilters } = useData();
  
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  // Enhanced data aggregation
  const chartData = useMemo(() => {
    const regionCount = {};
    const topicCount = {};
    const yearlyMetrics = {};
    const sectorData = {};
    const countryData = {};
    const cityData = {};
    const likelihoodByTopic = {};
    const relevanceByRegion = {};

    data.forEach(item => {
      if (item.region) regionCount[item.region] = (regionCount[item.region] || 0) + 1;
      if (item.topic) topicCount[item.topic] = (topicCount[item.topic] || 0) + 1;
      if (item.country) countryData[item.country] = (countryData[item.country] || 0) + 1;
      if (item.city) cityData[item.city] = (cityData[item.city] || 0) + 1;
      if (item.sector) sectorData[item.sector] = (sectorData[item.sector] || 0) + 1;
      
      if (item.year) {
        if (!yearlyMetrics[item.year]) {
          yearlyMetrics[item.year] = {
            intensity: { sum: 0, count: 0 },
            likelihood: { sum: 0, count: 0 },
            relevance: { sum: 0, count: 0 }
          };
        }
        if (item.intensity) {
          yearlyMetrics[item.year].intensity.sum += item.intensity;
          yearlyMetrics[item.year].intensity.count += 1;
        }
        if (item.likelihood) {
          yearlyMetrics[item.year].likelihood.sum += item.likelihood;
          yearlyMetrics[item.year].likelihood.count += 1;
        }
        if (item.relevance) {
          yearlyMetrics[item.year].relevance.sum += item.relevance;
          yearlyMetrics[item.year].relevance.count += 1;
        }
      }

      if (item.topic && item.likelihood) {
        if (!likelihoodByTopic[item.topic]) {
          likelihoodByTopic[item.topic] = { sum: 0, count: 0 };
        }
        likelihoodByTopic[item.topic].sum += item.likelihood;
        likelihoodByTopic[item.topic].count += 1;
      }

      if (item.region && item.relevance) {
        if (!relevanceByRegion[item.region]) {
          relevanceByRegion[item.region] = { sum: 0, count: 0 };
        }
        relevanceByRegion[item.region].sum += item.relevance;
        relevanceByRegion[item.region].count += 1;
      }
    });

    // Calculate averages
    const years = Object.keys(yearlyMetrics);
    const yearlyData = {
      intensity: years.map(year => yearlyMetrics[year].intensity.sum / yearlyMetrics[year].intensity.count),
      likelihood: years.map(year => yearlyMetrics[year].likelihood.sum / yearlyMetrics[year].likelihood.count),
      relevance: years.map(year => yearlyMetrics[year].relevance.sum / yearlyMetrics[year].relevance.count)
    };

    return {
      metricsOverTime: {
        labels: years,
        datasets: [
          {
            label: 'Intensity',
            data: yearlyData.intensity,
            borderColor: 'rgb(75, 192, 192)',
            tension: 0.1,
          },
          {
            label: 'Likelihood',
            data: yearlyData.likelihood,
            borderColor: 'rgb(255, 99, 132)',
            tension: 0.1,
          },
          {
            label: 'Relevance',
            data: yearlyData.relevance,
            borderColor: 'rgb(153, 102, 255)',
            tension: 0.1,
          }
        ],
      },
      regionChart: {
        labels: Object.keys(regionCount),
        datasets: [{
          data: Object.values(regionCount),
          backgroundColor: [
            'rgba(255, 99, 132, 0.8)',
            'rgba(54, 162, 235, 0.8)',
            'rgba(255, 206, 86, 0.8)',
            'rgba(75, 192, 192, 0.8)',
            'rgba(153, 102, 255, 0.8)',
          ],
        }],
      },
      topicChart: {
        labels: Object.keys(topicCount),
        datasets: [{
          label: 'Topics Distribution',
          data: Object.values(topicCount),
          backgroundColor: 'rgba(54, 162, 235, 0.8)',
        }],
      },
      countryChart: {
        labels: Object.keys(countryData).slice(0, 10),
        datasets: [{
          label: 'Top 10 Countries',
          data: Object.values(countryData).slice(0, 10),
          backgroundColor: 'rgba(153, 102, 255, 0.8)',
        }],
      },
      cityChart: {
        labels: Object.keys(cityData).slice(0, 10),
        datasets: [{
          label: 'Top 10 Cities',
          data: Object.values(cityData).slice(0, 10),
          backgroundColor: 'rgba(255, 159, 64, 0.8)',
        }],
      },
      sectorChart: {
        labels: Object.keys(sectorData),
        datasets: [{
          data: Object.values(sectorData),
          backgroundColor: [
            'rgba(255, 99, 132, 0.8)',
            'rgba(54, 162, 235, 0.8)',
            'rgba(255, 206, 86, 0.8)',
            'rgba(75, 192, 192, 0.8)',
            'rgba(153, 102, 255, 0.8)',
          ],
        }],
      },
    };
  }, [data]);

  // Enhanced filter options
  const filterOptions = useMemo(() => ({
    endYear: [...new Set(data.map(item => item.end_year))].filter(Boolean).sort(),
    topics: [...new Set(data.map(item => item.topic))].filter(Boolean).sort(),
    regions: [...new Set(data.map(item => item.region))].filter(Boolean).sort(),
    pest: [...new Set(data.map(item => item.pestle))].filter(Boolean).sort(),
    sources: [...new Set(data.map(item => item.source))].filter(Boolean).sort(),
    swot: [...new Set(data.map(item => item.swot))].filter(Boolean).sort(),
    countries: [...new Set(data.map(item => item.country))].filter(Boolean).sort(),
    cities: [...new Set(data.map(item => item.city))].filter(Boolean).sort(),
    sectors: [...new Set(data.map(item => item.sector))].filter(Boolean).sort(),
  }), [data]);

  const handleFilterChange = (e, filterType) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: e.target.value
    }));
  };

  return (
    <div className="p-6 space-y-6">
      {/* Enhanced Filters Section */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        <select
          className="p-2 border rounded"
          value={filters.endYear}
          onChange={(e) => handleFilterChange(e, 'endYear')}
        >
          <option value="all">End Year</option>
          {filterOptions.endYear.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.topic}
          onChange={(e) => handleFilterChange(e, 'topic')}
        >
          <option value="all">All Topics</option>
          {filterOptions.topics.map(topic => (
            <option key={topic} value={topic}>{topic}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.sector}
          onChange={(e) => handleFilterChange(e, 'sector')}
        >
          <option value="all">All Sectors</option>
          {filterOptions.sectors.map(sector => (
            <option key={sector} value={sector}>{sector}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.region}
          onChange={(e) => handleFilterChange(e, 'region')}
        >
          <option value="all">All Regions</option>
          {filterOptions.regions.map(region => (
            <option key={region} value={region}>{region}</option>
          ))}
        </select>
      </div>

      {/* Metrics Over Time */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">Metrics Over Time</h2>
        <div className="h-80">
          <Line options={options} data={chartData.metricsOverTime} />
        </div>
      </div>

      {/* Region Distribution */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">Region Distribution</h2>
        <div className="h-80">
          <Bar options={options} data={chartData.regionChart} />
        </div>
      </div>

      {/* Topic Distribution */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">Topic Distribution</h2>
        <div className="h-80">
          <Pie options={options} data={chartData.topicChart} />
        </div>
      </div>

      {/* Country Distribution */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">Country Distribution</h2>
        <div className="h-80">
          <Doughnut options={options} data={chartData.countryChart} />
        </div>
      </div>

      {/* City Distribution */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">City Distribution</h2>
        <div className="h-80">
          <Radar options={options} data={chartData.cityChart} />
        </div>
      </div>

      {/* Sector Distribution */}
      <div className="bg-white p-4 rounded-lg shadow">
        <h2 className="text-lg font-semibold mb-4">Sector Distribution</h2>
        <div className="h-80">
          <Pie options={options} data={chartData.sectorChart} />
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
