import React from 'react';
import { useData } from '../services/DataContext';
import { Line, Bar } from 'react-chartjs-2';
import 'chart.js/auto';

const Select = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <select
      value={value}
      onChange={(e) => onChange(e.target.value)}
      className="w-full border rounded p-2 focus:outline-none"
    >
      <option value="all">All {label}s</option>
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const DashboardApp = () => {
  const { data, filters, setFilters } = useData();

  const filteredData = data.filter((item) =>
    (filters.year === 'all' || item.year === filters.year) &&
    (filters.topic === 'all' || item.topic === filters.topic) &&
    (filters.region === 'all' || item.region === filters.region)
  );

  // Chart data and options with limited labels
  const lineChartData = {
    labels: filteredData.map((item) => item.year),
    datasets: [
      {
        label: 'Intensity',
        data: filteredData.map((item) => item.intensity),
        borderColor: '#3b82f6',
        backgroundColor: 'rgba(59, 130, 246, 0.2)',
      },
    ],
  };

  const lineChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false },
    },
    scales: {
      x: { display: true, maxTicksLimit: 10 },
      y: { beginAtZero: true },
    },
  };

  const barChartData = {
    labels: filteredData.slice(0, 10).map((item) => item.region),
    datasets: [
      {
        label: 'Relevance',
        data: filteredData.slice(0, 10).map((item) => item.relevance),
        backgroundColor: '#8b5cf6',
      },
    ],
  };

  const barChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: { display: false },
    },
    scales: {
      x: { display: true, maxTicksLimit: 5 },
      y: { beginAtZero: true },
    },
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8 text-center">Data Analysis Dashboard</h1>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <Select
          label="Year"
          options={['2020', '2021', '2022']}
          value={filters.year}
          onChange={(value) => setFilters((prev) => ({ ...prev, year: value }))}
        />
        <Select
          label="Topic"
          options={['AI', 'Healthcare', 'Climate']}
          value={filters.topic}
          onChange={(value) => setFilters((prev) => ({ ...prev, topic: value }))}
        />
        <Select
          label="Region"
          options={['North America', 'Europe', 'Asia']}
          value={filters.region}
          onChange={(value) => setFilters((prev) => ({ ...prev, region: value }))}
        />
      </div>

      {/* Chart Components */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6 h-96">
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <Line data={lineChartData} options={lineChartOptions} />
        </div>

        {/* Bar Chart */}
        <div className="bg-white shadow rounded-lg p-6 h-96">
          <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
          <Bar data={barChartData} options={barChartOptions} />
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
