import React, { useState } from 'react';
import { LineChart, Line, BarChart, Bar, PieChart, Pie, ScatterChart, Scatter, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell } from 'recharts';

// Sample data with multiple entries
const sampleData = [
  {
    year: 2020,
    intensity: 75,
    likelihood: 80,
    relevance: 85,
    country: "United States",
    region: "North America",
    topic: "AI"
  },
  {
    year: 2021,
    intensity: 65,
    likelihood: 70,
    relevance: 75,
    country: "United Kingdom",
    region: "Europe",
    topic: "Healthcare"
  },
  {
    year: 2022,
    intensity: 85,
    likelihood: 90,
    relevance: 95,
    country: "China",
    region: "Asia",
    topic: "Climate"
  }
];

// Custom Select Component
const Select = ({ label, options, value, onChange }) => {
  return (
    <div className="mb-4">
      <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
      <select
        value={value}
        onChange={(e) => onChange(e.target.value)}
        className="w-full border border-gray-300 rounded-md p-2 bg-white"
      >
        <option value="all">All {label}s</option>
        {options.map((option) => (
          <option key={option} value={option.toLowerCase()}>
            {option}
          </option>
        ))}
      </select>
    </div>
  );
};

const DashboardApp = () => {
  const [filters, setFilters] = useState({
    year: 'all',
    topic: 'all',
    region: 'all'
  });

  // Custom Card Component
  const Card = ({ children, className }) => (
    <div className={`bg-white rounded-lg shadow p-6 ${className}`}>
      {children}
    </div>
  );

  const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-gray-800 mb-8">Data Analysis Dashboard</h1>
      
      {/* Filters */}
      <Card className="mb-8">
        <h2 className="text-xl font-semibold mb-4">Filters</h2>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
          <Select
            label="Year"
            options={['2020', '2021', '2022']}
            value={filters.year}
            onChange={(value) => setFilters(prev => ({ ...prev, year: value }))}
          />
          <Select
            label="Topic"
            options={['AI', 'Healthcare', 'Climate']}
            value={filters.topic}
            onChange={(value) => setFilters(prev => ({ ...prev, topic: value }))}
          />
          <Select
            label="Region"
            options={['North America', 'Europe', 'Asia']}
            value={filters.region}
            onChange={(value) => setFilters(prev => ({ ...prev, region: value }))}
          />
        </div>
      </Card>

      {/* Key Metrics */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-8">
        <Card className="bg-blue-50">
          <h3 className="text-blue-600 font-medium">Average Intensity</h3>
          <p className="text-2xl font-bold text-blue-900 mt-2">
            {(sampleData.reduce((acc, curr) => acc + curr.intensity, 0) / sampleData.length).toFixed(1)}
          </p>
        </Card>
        <Card className="bg-green-50">
          <h3 className="text-green-600 font-medium">Average Likelihood</h3>
          <p className="text-2xl font-bold text-green-900 mt-2">
            {(sampleData.reduce((acc, curr) => acc + curr.likelihood, 0) / sampleData.length).toFixed(1)}
          </p>
        </Card>
        <Card className="bg-purple-50">
          <h3 className="text-purple-600 font-medium">Average Relevance</h3>
          <p className="text-2xl font-bold text-purple-900 mt-2">
            {(sampleData.reduce((acc, curr) => acc + curr.relevance, 0) / sampleData.length).toFixed(1)}
          </p>
        </Card>
        <Card className="bg-orange-50">
          <h3 className="text-orange-600 font-medium">Total Countries</h3>
          <p className="text-2xl font-bold text-orange-900 mt-2">
            {new Set(sampleData.map(d => d.country)).size}
          </p>
        </Card>
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <Card>
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <LineChart data={sampleData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="year" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Line type="monotone" dataKey="intensity" stroke="#3b82f6" strokeWidth={2} />
                <Line type="monotone" dataKey="likelihood" stroke="#10b981" strokeWidth={2} />
              </LineChart>
            </ResponsiveContainer>
          </div>
        </Card>

        {/* Bar Chart */}
        <Card>
          <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <BarChart data={sampleData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="region" />
                <YAxis />
                <Tooltip />
                <Legend />
                <Bar dataKey="relevance" fill="#8b5cf6" />
              </BarChart>
            </ResponsiveContainer>
          </div>
        </Card>

        {/* Pie Chart */}
        <Card>
          <h2 className="text-xl font-semibold mb-4">Topics Distribution</h2>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <PieChart>
                <Pie
                  data={sampleData}
                  dataKey="relevance"
                  nameKey="topic"
                  cx="50%"
                  cy="50%"
                  outerRadius={100}
                >
                  {sampleData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                  ))}
                </Pie>
                <Tooltip />
                <Legend />
              </PieChart>
            </ResponsiveContainer>
          </div>
        </Card>

        {/* Scatter Chart */}
        <Card>
          <h2 className="text-xl font-semibold mb-4">Intensity vs Likelihood</h2>
          <div className="h-80">
            <ResponsiveContainer width="100%" height="100%">
              <ScatterChart>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="intensity" name="Intensity" />
                <YAxis dataKey="likelihood" name="Likelihood" />
                <Tooltip cursor={{ strokeDasharray: '3 3' }} />
                <Legend />
                <Scatter name="Countries" data={sampleData} fill="#8884d8" />
              </ScatterChart>
            </ResponsiveContainer>
          </div>
        </Card>
      </div>
    </div>
  );
};

export default DashboardApp;
