import React from 'react';
import { useData } from '../services/DataContext';
import { Line, Bar, Pie, Scatter } from 'react-chartjs-2';
import 'chart.js/auto';

const Select = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <select
      value={value}
      onChange={(e) => onChange(e.target.value)}
      className="w-full border rounded p-2 focus:outline-none"
    >
      <option value="all">All {label}s</option>
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const DashboardApp = () => {
  const { data, filters, setFilters } = useData();

  // Define filteredData based on selected filters
  const filteredData = data.filter((item) =>
    (filters.year === 'all' || item.year === filters.year) &&
    (filters.topic === 'all' || item.topic === filters.topic) &&
    (filters.region === 'all' || item.region === filters.region) &&
    (filters.pestle === 'all' || item.pestle === filters.pestle) &&
    (filters.source === 'all' || item.source === filters.source) &&
    (filters.swot === 'all' || item.swot === filters.swot) &&
    (filters.country === 'all' || item.country === filters.country) &&
    (filters.city === 'all' || item.city === filters.city)
  );

  // Prepare chart data for Chart.js
  const lineChartData = {
    labels: filteredData.map((item) => item.year),
    datasets: [
      {
        label: 'Intensity',
        data: filteredData.map((item) => item.intensity),
        backgroundColor: 'rgba(59, 130, 246, 0.5)',
        borderColor: '#3b82f6',
        fill: true,
      },
      {
        label: 'Likelihood',
        data: filteredData.map((item) => item.likelihood),
        backgroundColor: 'rgba(16, 185, 129, 0.5)',
        borderColor: '#10b981',
        fill: true,
      },
    ],
  };

  const barChartData = {
    labels: filteredData.map((item) => item.region),
    datasets: [
      {
        label: 'Relevance',
        data: filteredData.map((item) => item.relevance),
        backgroundColor: '#8b5cf6',
      },
    ],
  };

  const pieChartData = {
    labels: filteredData.map((item) => item.topic),
    datasets: [
      {
        label: 'Topics Distribution',
        data: filteredData.map((item) => item.relevance),
        backgroundColor: ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'],
      },
    ],
  };

  const scatterChartData = {
    datasets: [
      {
        label: 'Intensity vs Likelihood',
        data: filteredData.map((item) => ({ x: item.intensity, y: item.likelihood })),
        backgroundColor: '#8884d8',
      },
    ],
  };

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8 text-center">Data Analysis Dashboard</h1>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-4 gap-4 mb-8">
        <Select
          label="Year"
          options={['2020', '2021', '2022']}
          value={filters.year}
          onChange={(value) => setFilters((prev) => ({ ...prev, year: value }))}
        />
        <Select
          label="Topic"
          options={['AI', 'Healthcare', 'Climate']}
          value={filters.topic}
          onChange={(value) => setFilters((prev) => ({ ...prev, topic: value }))}
        />
        <Select
          label="Region"
          options={['North America', 'Europe', 'Asia']}
          value={filters.region}
          onChange={(value) => setFilters((prev) => ({ ...prev, region: value }))}
        />
        <Select
          label="PEST"
          options={['Social', 'Economic', 'Environmental']}
          value={filters.pestle}
          onChange={(value) => setFilters((prev) => ({ ...prev, pestle: value }))}
        />
        <Select
          label="Source"
          options={['Report', 'Survey', 'Journal']}
          value={filters.source}
          onChange={(value) => setFilters((prev) => ({ ...prev, source: value }))}
        />
        <Select
          label="SWOT"
          options={['Strength', 'Opportunity', 'Threat']}
          value={filters.swot}
          onChange={(value) => setFilters((prev) => ({ ...prev, swot: value }))}
        />
        <Select
          label="Country"
          options={['USA', 'France', 'India']}
          value={filters.country}
          onChange={(value) => setFilters((prev) => ({ ...prev, country: value }))}
        />
        <Select
          label="City"
          options={['New York', 'Paris', 'Delhi']}
          value={filters.city}
          onChange={(value) => setFilters((prev) => ({ ...prev, city: value }))}
        />
      </div>

      {/* Chart Components */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <Line data={lineChartData} />
        </div>

        {/* Bar Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
          <Bar data={barChartData} />
        </div>

        {/* Pie Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Topics Distribution</h2>
          <Pie data={pieChartData} />
        </div>

        {/* Scatter Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity vs Likelihood</h2>
          <Scatter data={scatterChartData} />
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
