import React, { useState, useMemo } from 'react';
import { useData } from '../services/DataContext';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line, Bar, Pie, Doughnut } from 'react-chartjs-2';

// Register ChartJS components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

const DashboardApp = () => {
  const { data, filters, setFilters } = useData();
  
  // Chart.js options
  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
    },
  };

  // Aggregate data for different visualizations
  const chartData = useMemo(() => {
    const regionCount = {};
    const topicCount = {};
    const yearlyIntensity = {};
    const sectorData = {};

    data.forEach(item => {
      if (item.region) regionCount[item.region] = (regionCount[item.region] || 0) + 1;
      if (item.topic) topicCount[item.topic] = (topicCount[item.topic] || 0) + 1;
      if (item.year) {
        if (!yearlyIntensity[item.year]) {
          yearlyIntensity[item.year] = {
            intensity: 0,
            count: 0
          };
        }
        yearlyIntensity[item.year].intensity += item.intensity || 0;
        yearlyIntensity[item.year].count += 1;
      }
      if (item.sector) sectorData[item.sector] = (sectorData[item.sector] || 0) + 1;
    });

    // Calculate average intensity per year
    Object.keys(yearlyIntensity).forEach(year => {
      yearlyIntensity[year] = yearlyIntensity[year].intensity / yearlyIntensity[year].count;
    });

    return {
      regionChart: {
        labels: Object.keys(regionCount),
        datasets: [{
          data: Object.values(regionCount),
          backgroundColor: [
            'rgba(255, 99, 132, 0.8)',
            'rgba(54, 162, 235, 0.8)',
            'rgba(255, 206, 86, 0.8)',
            'rgba(75, 192, 192, 0.8)',
            'rgba(153, 102, 255, 0.8)',
          ],
        }],
      },
      topicChart: {
        labels: Object.keys(topicCount),
        datasets: [{
          label: 'Topics Distribution',
          data: Object.values(topicCount),
          backgroundColor: 'rgba(54, 162, 235, 0.8)',
        }],
      },
      intensityChart: {
        labels: Object.keys(yearlyIntensity),
        datasets: [{
          label: 'Average Intensity',
          data: Object.values(yearlyIntensity),
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1,
        }],
      },
      sectorChart: {
        labels: Object.keys(sectorData),
        datasets: [{
          data: Object.values(sectorData),
          backgroundColor: [
            'rgba(255, 99, 132, 0.8)',
            'rgba(54, 162, 235, 0.8)',
            'rgba(255, 206, 86, 0.8)',
            'rgba(75, 192, 192, 0.8)',
            'rgba(153, 102, 255, 0.8)',
          ],
        }],
      },
    };
  }, [data]);

  // Extract unique values for filters
  const filterOptions = useMemo(() => ({
    years: [...new Set(data.map(item => item.year))].filter(Boolean).sort(),
    topics: [...new Set(data.map(item => item.topic))].filter(Boolean).sort(),
    regions: [...new Set(data.map(item => item.region))].filter(Boolean).sort(),
    pestles: [...new Set(data.map(item => item.pestle))].filter(Boolean).sort(),
    sources: [...new Set(data.map(item => item.source))].filter(Boolean).sort(),
    countries: [...new Set(data.map(item => item.country))].filter(Boolean).sort(),
    sectors: [...new Set(data.map(item => item.sector))].filter(Boolean).sort(),
  }), [data]);

  const handleFilterChange = (e, filterType) => {
    setFilters(prev => ({
      ...prev,
      [filterType]: e.target.value
    }));
  };

  return (
    <div className="p-6">
      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 mb-6">
        <select
          className="p-2 border rounded"
          value={filters.year}
          onChange={(e) => handleFilterChange(e, 'year')}
        >
          <option value="all">All Years</option>
          {filterOptions.years.map(year => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.topic}
          onChange={(e) => handleFilterChange(e, 'topic')}
        >
          <option value="all">All Topics</option>
          {filterOptions.topics.map(topic => (
            <option key={topic} value={topic}>{topic}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.sector}
          onChange={(e) => handleFilterChange(e, 'sector')}
        >
          <option value="all">All Sectors</option>
          {filterOptions.sectors.map(sector => (
            <option key={sector} value={sector}>{sector}</option>
          ))}
        </select>

        <select
          className="p-2 border rounded"
          value={filters.region}
          onChange={(e) => handleFilterChange(e, 'region')}
        >
          <option value="all">All Regions</option>
          {filterOptions.regions.map(region => (
            <option key={region} value={region}>{region}</option>
          ))}
        </select>
        <select
          className="p-2 border rounded"
          value={filters. pestles}
          onChange={(e) => handleFilterChange(e, ' pestles')}
        >
          <option value="all">All  Pestles</option>
          {filterOptions.regions.map( pestles => (
            <option key={ pestles} value={ pestles}>{ pestles}</option>
          ))}
        </select>

      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Intensity Trend Over Years</h2>
          <div className="h-80">
            <Line options={options} data={chartData.intensityChart} />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Regional Distribution</h2>
          <div className="h-80">
            <Doughnut options={options} data={chartData.regionChart} />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Topics Distribution</h2>
          <div className="h-80">
            <Bar options={{
              ...options,
              indexAxis: 'y',
              plugins: {
                ...options.plugins,
                legend: {
                  display: false
                }
              }
            }} data={chartData.topicChart} />
          </div>
        </div>

        <div className="bg-white p-4 rounded-lg shadow">
          <h2 className="text-lg font-semibold mb-4">Sector Distribution</h2>
          <div className="h-80">
            <Pie options={options} data={chartData.sectorChart} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
