import React from 'react';
import { useData } from '../'; // Import the custom context
import {
  LineChart, Line, BarChart, Bar, PieChart, Pie, ScatterChart, Scatter,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell
} from 'recharts';

const Select = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <select value={value} onChange={(e) => onChange(e.target.value)} className="w-full border rounded p-2">
      <option value="all">All {label}s</option>
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const DashboardApp = () => {
  const { data, filters, setFilters } = useData(); // Access data and filters from context

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8">Data Analysis Dashboard</h1>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <Select
          label="Year"
          options={['2020', '2021', '2022']}
          value={filters.year}
          onChange={(value) => setFilters(prev => ({ ...prev, year: value }))}
        />
        <Select
          label="Topic"
          options={['AI', 'Healthcare', 'Climate']}
          value={filters.topic}
          onChange={(value) => setFilters(prev => ({ ...prev, topic: value }))}
        />
        <Select
          label="Region"
          options={['North America', 'Europe', 'Asia']}
          value={filters.region}
          onChange={(value) => setFilters(prev => ({ ...prev, region: value }))}
        />
        <Select
          label="PEST"
          options={['Social', 'Economic', 'Environmental']}
          value={filters.pestle}
          onChange={(value) => setFilters(prev => ({ ...prev, pestle: value }))}
        />
        <Select
          label="Source"
          options={['Report', 'Survey', 'Journal']}
          value={filters.source}
          onChange={(value) => setFilters(prev => ({ ...prev, source: value }))}
        />
        <Select
          label="SWOT"
          options={['Strength', 'Opportunity', 'Threat']}
          value={filters.swot}
          onChange={(value) => setFilters(prev => ({ ...prev, swot: value }))}
        />
        <Select
          label="Country"
          options={['USA', 'France', 'India']}
          value={filters.country}
          onChange={(value) => setFilters(prev => ({ ...prev, country: value }))}
        />
        <Select
          label="City"
          options={['New York', 'Paris', 'Delhi']}
          value={filters.city}
          onChange={(value) => setFilters(prev => ({ ...prev, city: value }))}
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="year" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="intensity" stroke="#3b82f6" strokeWidth={2} />
              <Line type="monotone" dataKey="likelihood" stroke="#10b981" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Bar Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={data}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="region" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="relevance" fill="#8b5cf6" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Pie Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Topics Distribution</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie data={data} dataKey="relevance" nameKey="topic" cx="50%" cy="50%" outerRadius={100}>
                {data.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={["#0088FE", "#00C49F", "#FFBB28"][index % 3]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Scatter Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity vs Likelihood</h2>
          <ResponsiveContainer width="100%" height={300}>
            <ScatterChart>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="intensity" name="Intensity" />
              <YAxis dataKey="likelihood" name="Likelihood" />
              <Tooltip cursor={{ strokeDasharray: '3 3' }} />
              <Legend />
              <Scatter name="Data Points" data={data} fill="#8884d8" />
            </ScatterChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
