// DashboardApp.js
import React, { useState } from 'react';
import {
  LineChart, Line, BarChart, Bar, PieChart, Pie, ScatterChart, Scatter,
  XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Cell
} from 'recharts';

const sampleData = [
  { year: 2020, intensity: 75, likelihood: 80, relevance: 85, region: "North America", topic: "AI" },
  { year: 2021, intensity: 65, likelihood: 70, relevance: 75, region: "Europe", topic: "Healthcare" },
  { year: 2022, intensity: 85, likelihood: 90, relevance: 95, region: "Asia", topic: "Climate" }
];

// Custom Select Component
const Select = ({ label, options, value, onChange }) => (
  <div className="mb-4">
    <label className="block text-sm font-medium text-gray-700 mb-1">{label}</label>
    <select value={value} onChange={(e) => onChange(e.target.value)} className="w-full border rounded p-2">
      <option value="all">All {label}s</option>
      {options.map((option) => (
        <option key={option} value={option}>{option}</option>
      ))}
    </select>
  </div>
);

const DashboardApp = () => {
  const [filters, setFilters] = useState({ year: 'all', topic: 'all', region: 'all',Pest:'all',Source:'all',Swot:'all',Country:'all',City:'all'});

  const filteredData = sampleData.filter(item => {
    return (
      (filters.year === 'all' || item.year === parseInt(filters.year)) &&
      (filters.topic === 'all' || item.topic === filters.topic) &&
      (filters.region === 'all' || item.region === filters.region)&&
      (filters.Pest === 'all' || item.Pest === filters.Pest)&&
      (filters.Source === 'all' || item.Source === filters.Source)&&
      (filters.Swot === 'all' || item.Swot === filters.Swot)&&
      (filters.Country === 'all' || item.Country === filters.Country)&&
      (filters.City === 'all' || item.City === filters.City)

    );
  });

  return (
    <div className="p-8 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-8">Data Analysis Dashboard</h1>

      {/* Filters */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
        <Select
          label="Year"
          options={['2020', '2021', '2022']}
          value={filters.year}
          onChange={(value) => setFilters(prev => ({ ...prev, year: value }))}
        />
        <Select
          label="Topic"
          options={['AI', 'Healthcare', 'Climate']}
          value={filters.topic}
          onChange={(value) => setFilters(prev => ({ ...prev, topic: value }))}
        />
        <Select
          label="Region"
          options={['North America', 'Europe', 'Asia']}
          value={filters.region}
          onChange={(value) => setFilters(prev => ({ ...prev, region: value }))}
        />
        <Select
          label="pestle"
          options={['Industries', 'Europe', 'Asia']}
          value={filters.pestle}
          onChange={(value) => setFilters(prev => ({ ...prev, Pest: value }))}
        />
      </div>

      {/* Charts */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Line Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity & Likelihood Trends</h2>
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={filteredData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="year" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="intensity" stroke="#3b82f6" strokeWidth={2} />
              <Line type="monotone" dataKey="likelihood" stroke="#10b981" strokeWidth={2} />
            </LineChart>
          </ResponsiveContainer>
        </div>

        {/* Bar Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Relevance by Region</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={filteredData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="region" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Bar dataKey="relevance" fill="#8b5cf6" />
            </BarChart>
          </ResponsiveContainer>
        </div>

        {/* Pie Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Topics Distribution</h2>
          <ResponsiveContainer width="100%" height={300}>
            <PieChart>
              <Pie data={filteredData} dataKey="relevance" nameKey="topic" cx="50%" cy="50%" outerRadius={100}>
                {filteredData.map((entry, index) => (
                  <Cell key={`cell-${index}`} fill={["#0088FE", "#00C49F", "#FFBB28"][index % 3]} />
                ))}
              </Pie>
              <Tooltip />
              <Legend />
            </PieChart>
          </ResponsiveContainer>
        </div>

        {/* Scatter Chart */}
        <div className="bg-white shadow rounded-lg p-6">
          <h2 className="text-xl font-semibold mb-4">Intensity vs Likelihood</h2>
          <ResponsiveContainer width="100%" height={300}>
            <ScatterChart>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="intensity" name="Intensity" />
              <YAxis dataKey="likelihood" name="Likelihood" />
              <Tooltip cursor={{ strokeDasharray: '3 3' }} />
              <Legend />
              <Scatter name="Data Points" data={filteredData} fill="#8884d8" />
            </ScatterChart>
          </ResponsiveContainer>
        </div>
      </div>
    </div>
  );
};

export default DashboardApp;
